e\\\": \\\"Global Imports\\\",\\r\\n                \\\"info\\\": \\\"A list of modules to import globally, e.g. [\'math\', \'numpy\'].\\\",\\r\\n            },\\r\\n        }\\r\\n\\r\\n    def get_globals(self, globals: list[str]) -> dict:\\r\\n        \\\"\\\"\\\"\\r\\n        Retrieves the global variables from the specified modules.\\r\\n\\r\\n        Args:\\r\\n            globals (list[str]): A list of module names.\\r\\n\\r\\n        Returns:\\r\\n            dict: A dictionary containing the global variables from the specified modules.\\r\\n        \\\"\\\"\\\"\\r\\n        global_dict = {}\\r\\n        for module in globals:\\r\\n            try:\\r\\n                imported_module = importlib.import_module(module)\\r\\n                global_dict[imported_module.__name__] = imported_module\\r\\n            except ImportError:\\r\\n                raise ImportError(f\\\"Could not import module {module}\\\")\\r\\n        return global_dict\\r\\n\\r\\n    def build(\\r\\n        self,\\r\\n        name: str = \\\"python_repl\\\",\\r\\n        description: str = \\\"A Python shell. Use this to execute python commands. Input should be a valid python command. If you want to see the output of a value, you should print it out with `print(...)`.\\\",\\r\\n        global_imports: list[str] = [\\\"math\\\"],\\r\\n    ) -> Tool:\\r\\n        \\\"\\\"\\\"\\r\\n        Builds a Python REPL tool.\\r\\n\\r\\n        Args:\\r\\n            name (str, optional): The name of the tool. Defaults to \\\"python_repl\\\".\\r\\n            description (str, optional): The description of the tool. Defaults to \\\"A Python shell. Use this to execute python commands. Input should be a valid python command. If you want to see the output of a value, you should print it out with `print(...)`. \\\".\\r\\n            global_imports (list[str], optional): A list of global imports to be available in the Python REPL. Defaults to [\\\"math\\\"].\\r\\n\\r\\n        Returns:\\r\\n            Tool: The built Python REPL tool.\\r\\n        \\\"\\\"\\\"\\r\\n        _globals = self.get_globals(global_imports)\\r\\n        python_repl = PythonREPL(_globals=_globals)\\r\\n        \\r\\n        class PersonalTool(Tool):\\r\\n            def _run(\\r\\n                self,\\r\\n                query: str,\\r\\n                run_manager: Optional[CallbackManagerForToolRun] = None,\\r\\n            ) -> Any:\\r\\n                \\\"\\\"\\\"Use the tool.\\\"\\\"\\\"\\r\\n                print(\\\"-----start---\\\")\\r\\n                \\r\\n                minio_client = MinioClient()\\r\\n                query = re.compile(\'plt.savefig(.*)\').sub(\\\"\\\", query)\\r\\n                \\r\\n                # 把图片存到本地\\r\\n                file_path = f\\\"/tmp/{int(time.time())}.png\\\"\\r\\n                query = query.replace(\\\"plt.show()\\\", f\\\"plt.savefig(\'{file_path}\')\\\")\\r\\n                query = \'import matplotlib\\\\nmatplotlib.rc(\\\"font\\\", family=\\\"WenQuanYi Zen Hei\\\")\\\\n\' + query\\r\\n                print(f\\\"-----exec code: {query}\\\")\\r\\n                \\r\\n                result = super(PersonalTool, self)._run(query, run_manager=run_manager)\\r\\n                print(\\\"-----code exec over-----\\\")\\r\\n                # 上传文件到minio\\r\\n                \\r\\n                minio_client.upload_minio(file_path, file_path)\\r\\n                file_url = minio_client.get_share_link(file_path)\\r\\n                file_url = minio_client.clear_minio_share_host(file_url)\\r\\n                print(f\\\"-----over---\\\", file_url)\\r\\n                return file_url\\r\\n        \\r\\n            async def _arun(\\r\\n                self,\\r\\n                query: str,\\r\\n                run_manager: Optional[AsyncCallbackManagerForToolRun] = None,\\r\\n            ) -> Any:\\r\\n                \\\"\\\"\\\"Use the tool asynchronously.\\\"\\\"\\\"\\r\\n                print(\\\"-----astart---\\\")\\r\\n                result = await super(PersonalTool, self)._arun(query, run_manager=run_manager)\\r\\n                print(\\\"---aover---\\\")\\r\\n                return result\\r\\n        \\r\\n        tool = PersonalTool(\\r\\n            name=name,\\r\\n            description=description,\\r\\n            func=python_repl.run,\\r\\n        )\\r\\n        return tool\\r\\n\\r\\n\", \"dynamic\": true, \"advanced\": false, \"password\": false, \"required\": true, \"file_path\": \"\", \"multiline\": true, \"title_case\": true, \"placeholder\": \"\", \"display_name\": \"Code\", \"model_config\": {}}, \"name\": {\"info\": \"The name of the tool.\", \"list\": false, \"name\": \"name\", \"show\": true, \"type\": \"str\", \"value\": \"python_repl\", \"dynamic\": false, \"advanced\": false, \"password\": false, \"required\": true, \"file_path\": \"\", \"multiline\": false, \"title_case\": true, \"placeholder\": \"\", \"display_name\": \"Name\", \"model_config\": {}}, \"_type\": \"CustomComponent\", \"description\": {\"info\": \"A description of the tool.\", \"list\": false, \"name\": \"description\", \"show\": true, \"type\": \"str\", \"value\": \"A Python shell. Use this to execute python commands. Input should be a valid python command. If you want to see the output of a value, you should print it out with `print(...)`.\", \"dynamic\": false, \"advanced\": false, \"password\": false, \"required\": true, \"file_path\": \"\", \"multiline\": false, \"title_case\": true, \"placeholder\": \"\", \"display_name\": \"Description\", \"model_config\": {}}, \"global_imports\": {\"info\": \"A list of modules to import globally, e.g. [\'math\', \'numpy\'].\", \"list\": true, \"name\": \"global_imports\", \"show\": true, \"type\": \"str\", \"value\": [\"math\"], \"dynamic\": false, \"advanced\": false, \"password\": false, \"required\": true, \"file_path\": \"\", \"multiline\": false, \"title_case\": true, \"placeholder\": \"\", \"display_name\": \"Global Imports\", \"model_config\": {}}}